import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-collaborative-editor',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="editor-container">
      <div class="editor-header">
        <div class="header-left">
          <button class="btn btn-outline" (click)="goBack()">← Back to Home</button>
          <h1>Collaborative Editor Demo</h1>
        </div>
        <div class="header-right">
          <div class="session-info">
            <span class="session-id">Demo Mode</span>
            <span class="control-status">Local Only</span>
          </div>
        </div>
      </div>

      <div class="editor-content">
        <div class="demo-panels">
          <!-- Text Input Demo -->
          <div class="demo-panel">
            <h3>📝 Form Input Demo</h3>
            <p>These form inputs will be synchronized in real-time once the server is connected:</p>
            <div class="form-group">
              <label>Name:</label>
              <input 
                type="text" 
                [(ngModel)]="demoForm.name"
                class="form-input"
                placeholder="Enter your name">
            </div>
            <div class="form-group">
              <label>Email:</label>
              <input 
                type="email" 
                [(ngModel)]="demoForm.email"
                class="form-input"
                placeholder="Enter your email">
            </div>
            <div class="form-group">
              <label>Message:</label>
              <textarea 
                [(ngModel)]="demoForm.message"
                class="form-textarea"
                rows="4"
                placeholder="Enter a message..."></textarea>
            </div>
          </div>

          <!-- Interactive Elements Demo -->
          <div class="demo-panel">
            <h3>🖱️ Interactive Elements</h3>
            <p>These interactions will be synchronized across all participants:</p>
            
            <div class="form-group">
              <label>Select Option:</label>
              <select [(ngModel)]="demoForm.selectedOption" class="form-select">
                <option value="">Choose...</option>
                <option value="option1">Option 1</option>
                <option value="option2">Option 2</option>
                <option value="option3">Option 3</option>
              </select>
            </div>

            <div class="form-group">
              <label>Checkboxes:</label>
              <div class="checkbox-group">
                <label class="checkbox-label">
                  <input type="checkbox" [(ngModel)]="demoForm.checkbox1">
                  Feature A
                </label>
                <label class="checkbox-label">
                  <input type="checkbox" [(ngModel)]="demoForm.checkbox2">
                  Feature B
                </label>
              </div>
            </div>

            <div class="button-group">
              <button class="btn btn-primary" (click)="showAlert('Primary button clicked!')">
                Primary Action
              </button>
              <button class="btn btn-secondary" (click)="showAlert('Secondary button clicked!')">
                Secondary Action
              </button>
              <button class="btn btn-success" (click)="addDynamicContent()">
                Add Dynamic Content
              </button>
            </div>
          </div>

          <!-- Dynamic Content Demo -->
          <div class="demo-panel">
            <h3>🔄 Dynamic Content</h3>
            <p>Dynamic changes will be synchronized:</p>
            
            <div class="dynamic-content">
              <div *ngFor="let item of dynamicItems; let i = index" class="dynamic-item">
                <span>{{ item.text }}</span>
                <button class="btn-small btn-danger" (click)="removeDynamicItem(i)">Remove</button>
              </div>
            </div>

            <div class="counter-demo">
              <h4>Counter: <span>{{ counter }}</span></h4>
              <button class="btn btn-outline" (click)="decrementCounter()">-</button>
              <button class="btn btn-outline" (click)="incrementCounter()">+</button>
