    const item = {
      text: `Dynamic item ${this.dynamicItems.length + 1} - Added at ${new Date().toLocaleTimeString()}`
    };
    this.dynamicItems.push(item);
  }

  removeDynamicItem(index: number) {
    this.dynamicItems.splice(index, 1);
  }

